package com.codemao.maomaster.gateway.exception;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.RestControllerAdvice;@RestControllerAdvicepublic class GlobalExceptionHandler {    private static final Logger logger = LoggerFactory.getLogger(GlobalExceptionHandler.class);    /**     * 处理自定义的业务异常     *     * @param e     * @return     */    @ExceptionHandler(value = BizException.class)    public Back bizExceptionHandler(BizException e) {        logger.error("发生业务异常！原因是：{}", e.getErrorMsg());        return new Back<>(Integer.valueOf(e.getErrorCode()), e.getErrorMsg(), null);    }    /**     * 处理空指针的异常     *     * @param e     * @return     */    @ExceptionHandler(value = NullPointerException.class)    public Back exceptionHandler(NullPointerException e) {        logger.error("发生空指针异常！原因是:", e);        return new Back(Result.BODY_NOT_MATCH);    }    /**     * 处理其他异常     *     * @param e     * @return     */    @ExceptionHandler(value = Exception.class)    public Back exceptionHandler(Exception e) {        logger.error("未知异常！原因是:", e);        return new Back<>(Result.INTERNAL_SERVER_ERROR);    }}